    font-size: 1rem;
    font-style: normal;
    font-weight: 400;
    line-height: 15px;
    letter-spacing: 0em;
    text-align: left;
    color: #000711;
  }
  
  
  .table.cart-items td.product-name a.btn-mini {
    display: none;
  }
  
  
  .table.cart-items tbody tr td.product-price .total-selling-price,
  .table.cart-items tbody tr td.product-price .best-price,
  .table.cart-items tbody tr td.quantity-price .total-selling-price,
  .table.cart-items tbody tr td.quantity-price .best-price {
    font-size: 1rem;
    font-style: normal;
    font-weight: 400;
    line-height: 20px;
    letter-spacing: 0em;
    text-align: left;
    color: #f71963;
  }
  
  .cart-more-options .srp-container .onda-v1 .blue {
    color: #f71963;
  }
  
  .srp-shipping-current-single__bullet path {
    fill: #f71963;
  }
  
  
  .item-attachments-content,
  .table.cart-items tbody tr.item-attachments-head,
  .table.cart-items tbody tr.item-attachments-content {
    background: #f8f7fc;
  }
  
  
  .item-attachment-name-label {
    font-size: 1rem;
    font-style: normal;
    font-weight: 400;
    line-height: 16px;
    letter-spacing: 0px;
    text-align: left;
    color: #a1a8b3;
    margin-bottom: 0.5rem;
  }
  
  
  .item-attachments-item-fields {
    padding-left: 0.75rem !important;
  }
  
  
  
  .table.cart-items i.icon.icon-remove.item-remove-ico:before {
    content: url('data:image/svg+xml;base64,');
    display: block;
  }
  
  
  /* Cart infos */
  
  .summary-template-holder tbody {
    border: none;
  }
  
  #cart-coupon-add {
    border: 1px solid #f71963;
    background: #f71963;
    font-size: 0;
    height: 30px;
  }
  
  #cart-coupon-add::before {
    color: white;
    content: 'Add';
    font-size: 0.75rem;
    font-style: normal;
    font-weight: 600;
    line-height: 32px;
    letter-spacing: 0em;
    text-align: left;
    height: 100% !important;
    display: flex;
    align-items: center;
    text-transform: uppercase;
  }
  
