    font-style: normal;
    font-weight: 400;
    line-height: 20px;
    letter-spacing: 0em;
    text-align: left;
    color: #5c5c5c;
  }
  
  .summary-template-holder tfoot tr td.monetary {
    font-size: 1.125rem;
    font-style: normal;
    font-weight: 400;
    line-height: 24px;
    letter-spacing: 0em;
    text-align: right;
    color: #f71963;
  }
  
  .btn-place-order-wrapper #cart-to-orderform,
  .btn-place-order-wrapper #cart-to-home {
    background: #f71963;
    border: 1px solid #f71963;
    font-family: Fabriga Medium, sans-serif;
    font-size: 14px;
    font-style: normal;
    font-weight: 600;
    line-height: 20px;
    letter-spacing: 0.5px;
    text-align: center;
    color: white;
    border-radius: 6px;
    margin-bottom: 1rem;
  }
  
  .btn-place-order-wrapper #cart-to-home {
