  .v-custom-step-payment
    .orderform-template
    .cart-template.mini-cart
    .summary-template-holder
    .summary
    > .summary-coupon-wrap,
  .v-custom-step-profile
    .orderform-template
    .cart-template.mini-cart
    .summary-template-holder
    .summary
    > .summary-coupon-wrap {
