    .summary-coupon
    fieldset.coupon-fieldset
    .coupon-data
    .orderform-template-holder
    .step
    a.submit.btn-success,
  .orderform-template-holder
    .step
    #payment-data
    .payment-discounts-list
    table
    td.action
    a.submit.btn-success,
  .orderform-template-holder
    #payment-data
    .payment-discounts-list
    table
    td.action
    .step
    a.submit.btn-success,
