    justify-content: start;
    width: 100%;
    max-width: 90%;
    padding: 20px 0;
  }
  
  .navbar-vtex-logo {
    margin-right: auto;
  }
  
  /* footer */
  
  .bf-footer {
    background: #142032;
    padding: 60px 0;
  }
  
  .footer-container {
    display: flex;
    justify-content: start;
    width: 100%;
    max-width: 90%;
