  }
  
  .bf-footer a {
    margin-left: 1rem;
    text-decoration: none;
    color: white;
    font-family: 'VTEX Trust', sans-serif;
    font-size: 14px;
    font-style: normal;
    font-weight: 400;
    line-height: 21px;
    letter-spacing: 0px;
    text-align: center;
  }
  
  @media screen and (max-width: 768px) {
    .footer-container {
      flex-direction: column;
      max-width: 90%;
      margin: 0 auto;
    }
  
    .footer-logo {
      margin-bottom: 2rem;
    }
  
    .bf-footer a {
      margin-left: 0;
      width: 50%;
      margin-bottom: 1rem;
      text-align: left;
    }
  
    .footer-links {
      display: flex;
      flex-wrap: wrap;
    }
  }
  
  /* custom */
  
  .button-custom-disable {
    pointer-events: none;
    cursor: not-allowed;
    opacity: 0.5;
  }
  
  
  body.v-custom-step-profile .client-pre-email h3.client-pre-email-h span:before, 
  body.v-custom-step-shipping .client-pre-email h3.client-pre-email-h span:before, 
  body.v-custom-step-shipping .client-profile-data .accordion-toggle>span:before, 
  body.v-custom-step-payment .client-pre-email h3.client-pre-email-h span:before, 
  body.v-custom-step-payment .client-profile-data .accordion-toggle>span:before, 
  body.v-custom-step-payment .shipping-data .accordion-toggle:before, 
  body.v-custom-step-email .client-pre-email h3.client-pre-email-h span:before, 
  body.v-custom-step-profile .client-profile-data .accordion-toggle>span:before, 
  body.v-custom-step-shipping .shipping-data .accordion-toggle:before, 
  body.v-custom-step-payment .payment-data .accordion-toggle>span:not(.payment-edit-link):before, 
  body.js-vcustom-hideEmailStep .client-profile-data .accordion-toggle>span:before, 
  .client-pre-email h3.client-pre-email-h span:before, 
  .client-profile-data .accordion-toggle>span:before, 
  .shipping-data .accordion-toggle:before, 
  .payment-data .accordion-toggle>span:not(.payment-edit-link):before,
  .summary-template-holder tr:not(.Shipping) .cart-active .table .srp-summary-result,
  tbody.totalizers-list.summary-template-holder tr:not(.Shipping) {
      display: none;
  }
  
  div#shipping-data {
      display: none;
  }

js/custom.js:
$(window).on("hashchange", function () {
    if (window.location.hash == "#/shipping") {
      fetch(
        `/api/checkout/pub/orderForm/${vtexjs.checkout.orderForm.orderFormId}/attachments/shippingData`,
        {
          body: '{"selectedAddresses":[{"addressType":"residential","receiverName":"VTEX","addressId":"","isDisposable":true,"postalCode":"10017","city":"New York","state":"NY","country":"USA","geoCoordinates":[-73.97682019999999,40.7572742],"street":"12 E 49th St","number":"","neighborhood":"Manhattan","complement":"","reference":null,"addressQuery":"12 E 49th St, New York, NY 10017, USA"}],"clearAddressIfPostalCodeNotFound":false}',
          method: "POST",
          mode: "cors",
          credentials: "include",
        }
      )
        .then((response) => response.json())
        .then(function (data) {
          vtexjs.checkout.getOrderForm().done(function() {
              window.location.href = '#/payment';
          });
        });
    }
  });
